#!/bin/bash
set -e

psql -v ON_ERROR_STOP=1 --username "$POSTGRES_USER" <<-EOSQL
    CREATE USER "$TECTONIC_IDENTITY_DB_USER" WITH PASSWORD '$TECTONIC_IDENTITY_DB_PASSWORD';
    CREATE DATABASE "$TECTONIC_IDENTITY_DB_NAME";
    GRANT ALL PRIVILEGES ON DATABASE "$TECTONIC_IDENTITY_DB_NAME" TO "$TECTONIC_IDENTITY_DB_USER";
EOSQL
